"use strict";

document.querySelector('.hero__btn').addEventListener('click', function () {
  console.log('click1');
  var targetElement = document.getElementById('result');
  var targetPosition = targetElement.getBoundingClientRect().top + window.pageYOffset - 70;
  window.scrollTo({
    top: targetPosition,
    behavior: 'smooth'
  });
});
